const cartLinks=document.querySelectorAll(".product-card-button-buy"),cartPopup=document.querySelector(".modal-cart"),cartClose=cartPopup.querySelector(".close-modal-cart"),continueLinks=cartPopup.querySelector(".continue-button");cartLinks.forEach(t=>{t.addEventListener("click",function(t){t.preventDefault(),cartPopup.classList.add("modal-show")})}),cartClose.addEventListener("click",function(t){t.preventDefault(),cartPopup.classList.remove("modal-show")}),continueLinks.addEventListener("click",function(t){t.preventDefault(),cartPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(t){27===t.keyCode&&cartPopup.classList.contains("modal-show")&&(t.preventDefault(),cartPopup.classList.remove("modal-show"))});
